async function handle(request) {
  const url = new URL(request.url)
  // Only intercept robots.txt requests
  if (url.pathname === '/robots.txt') {
    // Fetch origin (Cloudflare will forward)
    const originResp = await fetch(request)
    let body = await originResp.text()

    // Remove Cloudflare Managed block if present (case-insensitive)
    body = body.replace(/#\s*BEGIN\s*Cloudflare\s*Managed\s*content[\s\S]*?#\s*END\s*Cloudflare\s*Managed\s*Content\s*/gi, '')

    // Remove any Content-signal lines
    body = body.split(/\r?\n/).filter(line => !/^\s*content-signal\s*:/i.test(line)).join('\n')

    // Trim leading/trailing blank lines
    body = body.replace(/^\s+/, '').replace(/\s+$/, '') + '\n'

    // Build headers: keep minimal caching headers, ensure text/plain
    const headers = new Headers(originResp.headers)
    headers.set('Content-Type', 'text/plain; charset=utf-8')
    // Optionally: add Content-Signal as HTTP header (recommended instead of in-file)
    headers.set('Content-Signal', 'search=yes, ai-train=no')
    // Avoid serving a cached dirty copy: instruct minimal cache
    headers.set('Cache-Control', 'public, max-age=0, must-revalidate')

    return new Response(body, {
      status: 200,
      headers: headers
    })
  }

  // Default: pass through for everything else
  return fetch(request)
}

