export default {
  async fetch(request, env, ctx) {
    const url = new URL(request.url)

    if (url.pathname === '/robots.txt') {
      const originResp = await fetch(request)          // récupère la réponse d'origine
      let body = await originResp.text()

      // Supprime le bloc Cloudflare Managed content s'il existe
      body = body.replace(/#\s*BEGIN\s*Cloudflare\s*Managed\s*content[\s\S]*?#\s*END\s*Cloudflare\s*Managed\s*Content\s*/gi, '')

      // Supprime toute ligne Content-signal
      body = body.split(/\r?\n/).filter(line => !/^\s*content-signal\s*:/i.test(line)).join('\n')

      // Nettoyage final et saut de ligne final
      body = body.replace(/^\s+/, '').replace(/\s+$/, '') + '\n'

      // Prépare headers
      const headers = new Headers(originResp.headers)
      headers.set('Content-Type', 'text/plain; charset=utf-8')
      headers.set('Cache-Control', 'public, max-age=0, must-revalidate')
      // Header de debug : sert à prouver que le Worker s'est exécuté
      headers.set('X-Robots-Sanitized', 'yes')

      return new Response(body, { status: 200, headers })
    }

    return fetch(request)
  }
}

export default {
  async fetch(request, env, ctx) {
    const url = new URL(request.url);

    if (url.pathname === '/robots.txt') {
      try {
        // Remplace cette URL si ton repo/branch/path sont différents
        const rawUrl = 'https://raw.githubusercontent.com/paiedyali/paiedyali-landing/main/dist/robots.txt';

        // Récupère la version "raw" du fichier dans GitHub (ne passe pas par ton origine)
        const rawResp = await fetch(rawUrl, { cf: { cacheEverything: false } });

        if (!rawResp.ok) {
          // Si raw.githubusercontent échoue, fallback vers l'origine du site
          const originFallback = await fetch(request);
          const fallbackBody = await originFallback.text();
          const headersFallback = new Headers(originFallback.headers);
          headersFallback.set('Content-Type', 'text/plain; charset=utf-8');
          headersFallback.set('X-Robots-Source', 'origin-fallback');
          return new Response(fallbackBody, { status: originFallback.status, headers: headersFallback });
        }

        let body = await rawResp.text();

        // Nettoyage supplémentaire au cas où
        body = body.replace(/#\s*BEGIN\s*Cloudflare\s*Managed\s*content[\s\S]*?#\s*END\s*Cloudflare\s*Managed\s*Content\s*/gi, '');
        body = body.split(/\r?\n/).filter(line => !/^\s*content-signal\s*:/i.test(line)).join('\n');
        body = body.replace(/^\s+/, '').replace(/\s+$/, '') + '\n';

        const headers = new Headers(rawResp.headers);
        headers.set('Content-Type', 'text/plain; charset=utf-8');
        // Debug header pour confirmer que c'est bien la source Github qui a servi le fichier
        headers.set('X-Robots-Source', 'github-raw');
        // Contrôle du cache edge
        headers.set('Cache-Control', 'public, max-age=0, must-revalidate');

        return new Response(body, { status: 200, headers });
      } catch (err) {
        return new Response('User-agent: *\nAllow: /\n', { status: 200, headers: { 'Content-Type': 'text/plain; charset=utf-8' }});
      }
    }

    return fetch(request);
  }
}

